Parameters.ACTIVE_BALANCE_SYSTEM_CODE:
(* No active balance - active it ! *)
(A:FUELSYSTEM TRIGGER STATUS:#TRIGGER_ID_ENDBALANCE#, Bool) ! if{
	(A:FUELSYSTEM TANK QUANTITY:#TANK_ID_LEFT#, Gallons) (A:FUELSYSTEM TANK QUANTITY:#TANK_ID_RIGHT#, Gallons) < if{
		1 #SET_R_TO_L_TRIGGER#
		0 #SET_L_TO_R_TRIGGER#
	} els{
		1 #SET_L_TO_R_TRIGGER#
		0 #SET_R_TO_L_TRIGGER#
	}
}

UseInputEvent.ENUM_VAL_TO_POS_EXTERNAL:
l0 #FUEL_SELECTOR_ENUM_POS_UP# == if{ #POS_UP# sp2 g2 }
l0 #FUEL_SELECTOR_ENUM_POS_CROSSFEED# == if{ #POS_CROSSFEED# sp2 g2 }
l0 #FUEL_SELECTOR_ENUM_POS_DOWN# == if{ #POS_DOWN# sp2 g2 }
(>K:#KEY_POS_UP#)
:2 l2
