UseTemplate.SET_STATE_EXTERNAL:
(A:AUTOPILOT DISENGAGED, Bool) ! #TEST_CODE# and  if{
#RESET_CODE#
#TOGGLE_STATE_CODE#
#AP_STATE_CHANGED_TEST# if{
(>K:AP_MASTER)
(A:AUTOPILOT MASTER, Bool) ! if{
#MANUAL_OFF_CODE# #ON_AP_MASTER_SWITCH_OFF#
} els{ #ON_AP_MASTER_SWITCH_ON# }
}
}

Condition.AUTO_TOGGLE_HALF_BANK_CODE:
(A:AUTOPILOT MASTER, Bool) if{
(A:INDICATED ALTITUDE, feet) #ACTIVE_ABOVE_ALT_FT# > (>O:_Is_Over_#ACTIVE_ABOVE_ALT_FT#_FT)
(O:_Is_Over_#ACTIVE_ABOVE_ALT_FT#_FT) (O:_Was_Over_#ACTIVE_ABOVE_ALT_FT#_FT) != sp0
(O:_Is_Over_#ACTIVE_ABOVE_ALT_FT#_FT) (>O:_Was_Over_#ACTIVE_ABOVE_ALT_FT#_FT)
l0 if{ (O:_Is_Over_#ACTIVE_ABOVE_ALT_FT#_FT) (>K:AP_MAX_BANK_SET) }
}

UseTemplate.UPDATE_CODE:
(A:AUTOPILOT MASTER, Bool) ! if{
(A:AUTOPILOT WING LEVELER, Bool) if{
(>K:AP_WING_LEVELER_OFF)
(>K:AP_PITCH_LEVELER_OFF)
}
}

UseTemplate.SET_STATE_EXTERNAL:
(>K:AP_WING_LEVELER)
(A:AUTOPILOT WING LEVELER, Bool) if{
(A:AUTOPILOT MASTER, Bool) (>O:APStateWhenLevelerEnabled)
(A:AUTOPILOT MASTER, Bool) ! if{
(>K:AUTOPILOT_ON)
}
(>K:AP_PITCH_LEVELER_ON)
} els{
(>K:AP_PITCH_LEVELER_OFF)
(O:APStateWhenLevelerEnabled) if{
(>K:AUTOPILOT_ON)
} els{
(>K:AUTOPILOT_OFF)
}
}

False.SET_STATE_EXTERNAL:
#ID# (>K:AUTO_THROTTLE_ARM)
(A:FLAPS HANDLE INDEX, Number) 0 > if{
(A:DESIGN SPEED VS0, knots) sp1
} els{
(A:DESIGN SPEED VS1, knots) sp1
}
l1 1.3 * (A:AUTOPILOT AIRSPEED HOLD VAR:0, knots) max (>K:AP_SPD_VAR_SET)

Case.SET_STATE_EXTERNAL:
(A:AUTOPILOT AIRSPEED HOLD, Bool) (A:AUTOPILOT MACH HOLD, Bool) or if{
	(>K:AP_AIRSPEED_OFF)
} els{
	(A:FLAPS HANDLE INDEX, Number) 0 == if{
		(>K:AP_PANEL_SPEED_HOLD_TOGGLE)
	} els{
		(A:DESIGN SPEED VS1, knots) sqr (A:TOTAL WEIGHT, pounds) (A:MAX GROSS WEIGHT, pounds) / * sqrt (>K:AP_SPD_VAR_SET)
		(>K:AP_AIRSPEED_ON)
	}
}

Parameters.SET_STATE_EXTERNAL:
1 -1 p0 0 > ? sp0
#AIRSPEED_IS_IN_MACH# if{
#INCREMENT_INDEX# (A:AUTOPILOT MACH HOLD VAR:#INCREMENT_INDEX#, mach) #MACH_INCREMENT# l0 * + 100 * (>K:2:AP_MACH_VAR_SET)
} els{
#INCREMENT_INDEX# (A:AUTOPILOT AIRSPEED HOLD VAR:#INCREMENT_INDEX#, knots) #KNOTS_INCREMENT# l0 * + 0 max (>K:2:AP_SPD_VAR_SET)
}

UseTemplate.UPDATE_CODE:
(A:AUTOPILOT FLIGHT LEVEL CHANGE, Bool) ! if{
(L:XMLVAR_AirSpeedIsInMach) if{
(>K:AP_MACH_ON)
} els{
(>K:AP_AIRSPEED_ON)
}
}

UseTemplate.SET_STATE_ON:
1 (>L:XMLVAR_SpeedIsManuallySet)
(A:FLAPS HANDLE INDEX, Number) 0 > if{
(A:DESIGN SPEED VS0, knots) sp1
} els{
(A:DESIGN SPEED VS1, knots) sp1
}
l1 1.3 * (A:AIRSPEED INDICATED, knots) max (>K:AP_SPD_VAR_SET)
